package core_java;

public class IfElseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		
		if(a==10) 
		{
			System.out.println("a is 10");
		}
		else if(a<10)
		{
			System.out.println("a is less than 10");
		}
		else 
		{
			System.out.println("a is greater than 10");
		}

	}

}