package core_java;

public class WhileloopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//While example
		int a = 10;
		
		while(a>=0)
		{
			System.out.println(a);
			a--;
		}
		
		
		//Do While Example
		int b = 10;
		do
		{
			System.out.println(b);
			b--;
		}
		while(b>=0);
		
		

	}

}